import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:movie_db/core/model/movie_response.dart';import 'package:movie_db/core/service.dart';class HomeScreen extends StatefulWidget {  @override  _HomeScreenState createState() => _HomeScreenState();}class _HomeScreenState extends State<HomeScreen> {  Service _service;  var imageUrl = 'http://image.tmdb.org/t/p/w500';  @override  void initState() {    print('init HomeScreen');    _service = Service();    super.initState();  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(),      body: FutureBuilder<MovieResponse>(          future: _service.getNowPlaying(),          builder: (context, snapshot) {            if (snapshot.hasData) {              return ListView.builder(                itemCount: snapshot.data.results.length,                itemBuilder: (context, index) {                  ///Tambahin ini                  var data = snapshot.data.results[index];                  return snapshot.data.results == null                      ? Text('Tidak ada Data ')                      : Padding(                          padding: const EdgeInsets.all(8.0),                          child: Row(                            mainAxisSize: MainAxisSize.max,                            children: <Widget>[                              ///Gambar                              Flexible(                                flex: 1,                                fit: FlexFit.tight,                                child: Container(                                  decoration: BoxDecoration(                                      borderRadius: BorderRadius.all(                                          Radius.circular(10))),                                  height: 150,                                  width: 100,                                  child:                                      snapshot.data.results[index].posterPath !=                                              null                                          ? ClipRRect(                                              borderRadius: BorderRadius.all(                                                  Radius.circular(10)),                                              child: Image.network(                                                imageUrl + data.posterPath,                                                fit: BoxFit.cover,                                              ))                                          : Icon(Icons.home),                                ),                              ),                              SizedBox(                                width: 10,                              ),                              ///Title                              Flexible(                                  flex: 2,                                  fit: FlexFit.tight,                                  child: Container(                                    height: 150,                                    decoration: BoxDecoration(                                        borderRadius: BorderRadius.all(                                            Radius.circular(10)),                                        color: Colors.white,                                        boxShadow: [                                          BoxShadow(                                              color: Colors.blue, blurRadius: 3)                                        ]),                                    child: Padding(                                      padding: const EdgeInsets.all(8.0),                                      child: Column(                                        mainAxisSize: MainAxisSize.max,                                        children: <Widget>[                                          Text(                                            data.title,                                            style: TextStyle(                                                fontSize: 20,                                                fontWeight: FontWeight.bold),                                          ),                                          SizedBox(                                            height: 10,                                          ),                                          Text(                                            data.overview,                                            overflow: TextOverflow.ellipsis,                                            maxLines: 3,                                          )                                        ],                                      ),                                    ),                                  ))                            ],                          ),                        );                },              );            } else {              return Center(                child: CircularProgressIndicator(),              );            }          }),    );  }}